import React, { useState, useEffect } from 'react';
import DataTable from '../components/DataTable';
import IndividualForm from '../components/IndividualForm';
import { PlusIcon } from '@heroicons/react/24/outline';

const IndividualsPage = () => {
  const [individuals, setIndividuals] = useState([]);
  const [selectedIndividual, setSelectedIndividual] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Mock data for demonstration
  useEffect(() => {
    // In a real application, this would be an API call
    const mockIndividuals = [
      { id: 1, name: 'John Doe', email: 'john@example.com', phone: '555-123-4567', address: '123 Main St, Anytown, USA', notes: 'Department: Engineering' },
      { id: 2, name: 'Jane Smith', email: 'jane@example.com', phone: '555-987-6543', address: '456 Oak Ave, Somewhere, USA', notes: 'Department: Marketing' },
      { id: 3, name: 'Bob Johnson', email: 'bob@example.com', phone: '555-456-7890', address: '789 Pine Rd, Nowhere, USA', notes: 'Department: Finance' }
    ];

    setIndividuals(mockIndividuals);
  }, []);

  const handleAddIndividual = () => {
    setSelectedIndividual(null);
    setShowForm(true);
  };

  const handleEditIndividual = (individual) => {
    setSelectedIndividual(individual);
    setShowForm(true);
  };

  const handleSaveIndividual = (formData) => {
    if (selectedIndividual) {
      // Update existing individual
      const updatedIndividuals = individuals.map(individual => 
        individual.id === selectedIndividual.id 
          ? { ...individual, ...formData } 
          : individual
      );
      setIndividuals(updatedIndividuals);
    } else {
      // Add new individual
      const newIndividual = {
        id: individuals.length + 1,
        ...formData
      };
      setIndividuals([...individuals, newIndividual]);
    }

    setShowForm(false);
    setSelectedIndividual(null);
  };

  const handleCancelForm = () => {
    setShowForm(false);
    setSelectedIndividual(null);
  };

  const columns = [
    { key: 'name', label: 'Name' },
    { key: 'email', label: 'Email' },
    { key: 'phone', label: 'Phone' }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8 text-gray-900 dark:text-white">Manage Individuals</h1>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-gray-900 dark:text-white">Individuals</h2>
            <button 
              onClick={handleAddIndividual}
              className="btn btn-primary inline-flex items-center"
            >
              <PlusIcon className="h-5 w-5 mr-1" />
              Add Individual
            </button>
          </div>

          <DataTable
            data={individuals}
            columns={columns}
            title=""
            onViewItem={handleEditIndividual}
            onExportItem={() => {}}
          />
        </div>

        {showForm && (
          <div>
            <IndividualForm 
              individual={selectedIndividual}
              onSave={handleSaveIndividual}
              onCancel={handleCancelForm}
            />
          </div>
        )}

        {!showForm && (
          <div className="card flex flex-col items-center justify-center p-8 h-full">
            <div className="text-center">
              <PlusIcon className="h-16 w-16 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                Manage Individual Information
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-6">
                Select an individual to edit their information or add a new individual.
              </p>
              <button 
                onClick={handleAddIndividual}
                className="btn btn-primary inline-flex items-center"
              >
                <PlusIcon className="h-5 w-5 mr-1" />
                Add Individual
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default IndividualsPage;
